mapper.select:
SELECT CASE WHEN X.UPPER_MENU_NAME IS NOT NULL THEN X.UPPER_MENU_NAME ELSE X.MENU_NM END MENU_NM , X.MENU_ID , X.MENU_URL , X.USER_ID , X.USER_NM , X.USER_DEPT_CODE FROM ( SELECT DISTINCT (SELECT TC.NAME FROM TB_CODE TC WHERE TC.CODE_ID = 'IG30' AND TC.CODE=TM.UPPER_MENU_CODE) AS UPPER_MENU_NAME , TM.UPPER_MENU_CODE AS UPPER_MENU_CODE , NULL AS MENU_ID , NULL AS MENU_NM , NULL AS MENU_URL , '0' AS MENU_DISPLAY_ORDER , TU.USER_ID , TU.USER_NM , TU.USER_DEPT_CODE FROM TB_USER TU INNER JOIN TB_USER_AUTH TUA ON TU.USER_ID = TUA.USER_ID INNER JOIN TB_FUNC_CLASS TFC ON TUA.USER_DEPT_CODE = TFC.USER_DEPT_CODE AND TUA.FUNC_AUTH_CODE = TFC.FUNC_AUTH_CODE AND TFC.MENU_YN='Y' INNER JOIN TB_MENU TM ON TFC.MENU_ID = TM.MENU_ID WHERE TU.USER_ID = #{USER_ID}
UNION ALL
SELECT NULL AS UPPER_MENU_NAME , TM.UPPER_MENU_CODE AS UPPER_MENU_CODE , TM.MENU_ID AS MENU_ID , TM.MENU_NM AS MENU_NM , TM.MENU_URL AS MENU_URL , TM.MENU_DISPLAY_ORDER AS MENU_DISPLAY_ORDER , TU.USER_ID , TU.USER_NM , TU.USER_DEPT_CODE FROM TB_USER TU INNER JOIN TB_USER_AUTH TUA ON TU.USER_ID = TUA.USER_ID INNER JOIN TB_FUNC_CLASS TFC ON TUA.USER_DEPT_CODE = TFC.USER_DEPT_CODE AND TUA.FUNC_AUTH_CODE = TFC.FUNC_AUTH_CODE AND TFC.MENU_YN='Y' INNER JOIN TB_MENU TM ON TFC.MENU_ID = TM.MENU_ID WHERE TU.USER_ID = #{USER_ID} ) X GROUP BY X.UPPER_MENU_CODE , X.MENU_ID , X.MENU_NM , X.MENU_URL , X.USER_ID , X.USER_NM , X.USER_DEPT_CODE

            
  			 SELECT 
			     FUNC_AUTH_CODE
			    ,FUNC_AUTH_NM
			FROM TB_FUNC_CLASS 
			WHERE USER_DEPT_CODE = #{USER_DEPT_CODE} 
            And MENU_YN =#{MENU_YN}
