mapper.update:
       Update smAs
      		Set
 				asState = #{asState}
 				,stateUpdateUserId = #{stateUpdateUserId}
 				,stateUpdateDateTime = now()
		Where idx = #{idx}

mapper.insert:
  	Insert into smAsResult( 
        	 idx
            ,userId
            ,groupId
            ,asResult 
            ,asResultDateTime
            )
        Values (
              #{idx}
	  		, #{userId}
	  		, #{groupId}
	  		, #{asResult}
	  		, now()
	  		)


  	Insert into smAsHistory( 
        	 userId
            ,groupId
            ,asHistory
            ,asHistoryDateTime
            ,upidx
            )
        Values (
	  		  #{userId}
	  		, #{groupId}
	  		, #{asHistory}
	  		, now()
	  		, #{upidx}
	  		)
